import api from "../api";
import { Service, ServiceCredentials } from "./service-types";

export default new class ServiceService {
    async createService (credentials: ServiceCredentials) {
        if(!credentials.technology || !credentials.material || !credentials.images) {
            return;
        }

        console.log(credentials);

        const formData = new FormData();

        formData.append('name', credentials.name);
        formData.append('desc', credentials.desc);
        for (let i = 0; i < credentials.images.length; i++) {
            formData.append('images', credentials.images[i]);
        }

        try {
            return await api.post('/service', formData, {
              headers: { 'Content-Type': 'multipart/form-data' },
            });
          } catch (error) {
            console.error(error);
            alert('Failed to create service');
          }
    }
}
