import React, { useState } from "react";
import api from "../api";

interface OrderStatusUpdateProps {
  orderId: string;
  currentStatus: string;
  onStatusUpdated: (newStatus: string) => void;
}

const allowedStatuses = ['pending', 'accepted', 'payed', 'sent', 'recieved'];

export default function OrderStatusUpdate({
  orderId,
  currentStatus,
  onStatusUpdated,
}: OrderStatusUpdateProps) {
  // We filter out the current status so that the user cannot reselect it.
  const options = allowedStatuses.filter((status) => status !== currentStatus);
  const [selectedStatus, setSelectedStatus] = useState(currentStatus);

  const handleUpdate = async () => {
    try {
      const res = await api.patch(`/order/${orderId}/status`, { status: selectedStatus });
      onStatusUpdated(res.data.order.status);
    } catch (error) {
      console.error("Error updating status", error);
    }
  };

  return (
    <div>
      <h3>Update Order Status</h3>
      <select value={selectedStatus} onChange={(e) => setSelectedStatus(e.target.value)}>
        {options.map((status) => (
          <option key={status} value={status}>
            {status}
          </option>
        ))}
      </select>
      <button onClick={handleUpdate}>Update Status</button>
    </div>
  );
}
