import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import api from "../api";

// Define the shape of a brief service record
interface ServiceBrief {
  _id: string;
  name: string;
  technology: { name: string };
  material: { name: string };
  images: { data: string; contentType: string }[];
}

export default function ServicesListPage() {
  // States for the list of services and search filters.
  const [services, setServices] = useState<ServiceBrief[]>([]);
  const [searchName, setSearchName] = useState<string>("");
  const [searchTechnology, setSearchTechnology] = useState<string>("");
  const [searchMaterial, setSearchMaterial] = useState<string>("");

  // Fetch services from the API using the current search filters.
  const fetchServices = async () => {
    try {
      const response = await api.get("/service", {
        params: {
          name: searchName,
          technology: searchTechnology,
          material: searchMaterial,
        },
      });
      // Assuming the API returns an object like { services: [...] }
      setServices(response.data.services);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  // Option 1: Automatically fetch services whenever the search parameters change.
  useEffect(() => {
    fetchServices();
  }, [searchName, searchTechnology, searchMaterial]);

  // Option 2: Alternatively, you can use a "Search" button instead of auto‑fetching.
  // const handleSearch = () => {
  //   fetchServices();
  // };

  return (
    <div style={{ padding: "1rem" }}>
      <h1>Browse Services</h1>
      <div style={{ marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder="Search by name"
          value={searchName}
          onChange={(e) => setSearchName(e.target.value)}
          style={{ marginRight: "0.5rem" }}
        />
        <input
          type="text"
          placeholder="Search by technology"
          value={searchTechnology}
          onChange={(e) => setSearchTechnology(e.target.value)}
          style={{ marginRight: "0.5rem" }}
        />
        <input
          type="text"
          placeholder="Search by material"
          value={searchMaterial}
          onChange={(e) => setSearchMaterial(e.target.value)}
          style={{ marginRight: "0.5rem" }}
        />
        {/* If you prefer a button-triggered search instead of auto‑search, uncomment below */}
        {/* <button onClick={handleSearch}>Search</button> */}
      </div>

      {/* Display services as cards in a responsive grid */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
          gap: "1rem",
        }}
      >
        {services.map((service) => {
          // Use the first image (if available) to create a preview URL.
          const firstImageUrl =
            service.images.length > 0
              ? `data:${service.images[0].contentType};base64,${service.images[0].data}`
              : "";
          return (
            <div
              key={service._id}
              style={{
                border: "1px solid #ccc",
                borderRadius: "4px",
                padding: "1rem",
                textAlign: "center",
              }}
            >
              <h2>{service.name}</h2>
              {firstImageUrl ? (
                <img
                  src={firstImageUrl}
                  alt={service.name}
                  style={{ width: "100%", height: "auto", marginBottom: "0.5rem" }}
                />
              ) : (
                <div style={{ height: "150px", backgroundColor: "#f0f0f0", marginBottom: "0.5rem" }}>
                  No Image
                </div>
              )}
              <p>
                <strong>Technology:</strong> {service.technology.name}
              </p>
              <p>
                <strong>Material:</strong> {service.material.name}
              </p>
              <Link to={`/service/${service._id}`}>
                <button style={{ marginTop: "0.5rem" }}>View Details</button>
              </Link>
            </div>
          );
        })}
      </div>
    </div>
  );
}
