import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api";
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";

interface UploadedImage {
  original: string;
  thumbnail: string;
}

interface Service {
  _id: string;
  name: string;
  technology: { name: string };
  material: { name: string };
  images: { data: number[]; contentType: string }[];
}

export default function ServiceListPage() {
  const [services, setServices] = useState<Service[]>([]);

  useEffect(() => {
    async function fetchServices() {
      try {
        const res = await api.get("/services");
        setServices(res.data.services);
      } catch (error) {
        console.error("Failed to fetch services", error);
      }
    }
    fetchServices();
  }, []);

  return (
    <div>
      <h1>Available Services</h1>
      <div className="service-list">
        {services.map(service => {
          const galleryImages: UploadedImage[] = service.images.length
            ? [{
                original: `data:${service.images[0].contentType};base64,${btoa(
                  String.fromCharCode(...service.images[0].data)
                )}`,
                thumbnail: `data:${service.images[0].contentType};base64,${btoa(
                  String.fromCharCode(...service.images[0].data)
                )}`,
              }]
            : [];

          return (
            <div key={service._id} className="service-block">
              <h2>{service.name}</h2>
              <p><strong>Technology:</strong> {service.technology.name}</p>
              <p><strong>Material:</strong> {service.material.name}</p>

              {galleryImages.length > 0 && (
                <div className="image-preview">
                  <ImageGallery items={galleryImages} showThumbnails={false} showPlayButton={false} />
                </div>
              )}

              <Link to={`/service-details/${service._id}`}>
                <button>View Details</button>
              </Link>
            </div>
          );
        })}
      </div>
    </div>
  );
}
